func majorityElement(input: [Int]) -> Int? {
    var counts = [Int:Int]()
    for element in input {
    	if let value = counts[element] {
	    counts[element] = value + 1
    	} else {
       	    counts[element] = 1
    	}
    }

    var result: Int?
    for (element, count) in counts {
    	if count > input.count / 2 {
            result = element
    	}
    }

    return result
}

